import React, {useState} from 'react'
import './ExpenseForm.css'

const ExpenseForm = (props) => {
    const [enteredTitle,setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('')
    const [form,setForm] = useState(false)

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    }

    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    }

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    }

    function showForm(){
        setForm(true)
    }

    function hideForm(){
        setForm(false)
    }

    const submitHandler = (event) => {
        event.preventDefault();  /*Built in JS which prevents the request being sent 
        and thus page does not get reloaded when Add Expense button is clicked */

        const expenseData = {
            title : enteredTitle,
            amount : enteredAmount,
            date : new Date(enteredDate)
        }

        props.onSaveExpenseData(expenseData)
        // Two-way binding
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
    }

    return <form onSubmit={submitHandler}>
        {form === true ? <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" 
                    value={enteredTitle} //this changes the input when we change the state
                    onChange={titleChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" 
                    min="0.01" 
                    step="0.01" 
                    value={enteredAmount}
                    onChange={amountChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" 
                    min="2021-01-01"
                    max="2023-12-31" 
                    value={enteredDate}
                    onChange={dateChangeHandler}/>
            </div>
        </div> : <div className="add_new--expense"><button onClick={showForm}>Add Expense</button></div>}

        <div className="new-expense__actions">
            <button onClick={hideForm}>Cancel</button>
            <button type="submit" onClick={hideForm}>Add Expense</button>
        </div>
    </form>
}

export default ExpenseForm;
